# [doc = " Unpack the given 256-bit vector into its 64-bit components. The first"] # [doc = " element of the array returned corresponds to the least significant 64-bit"] # [doc = " lane in `a`."] # [target_feature (enable = "avx2")] pub fn unpack64x256 (a : __m256i) -> [u64 ; 4] { let lo = _mm256_extracti128_si256 (a , 0) ; let hi = _mm256_extracti128_si256 (a , 1) ; [_mm_cvtsi128_si64 (lo) as u64 , _mm_cvtsi128_si64 (_mm_srli_si128 (lo , 8)) as u64 , _mm_cvtsi128_si64 (hi) as u64 , _mm_cvtsi128_si64 (_mm_srli_si128 (hi , 8)) as u64 ,] }